mapper.select:
		SELECT USER_NUM, USER_ID, USER_PW, USER_NAME, USER_NICK, USER_EMAIL, USER_TEL, USER_IMG_EXP
      	  FROM USER_INFO
         WHERE USER_ID = #{userId}

SELECT POST_CATEGORY, POST_TITLE
  FROM POST_INFO
 WHERE POST_USER_NUM = #{postUserNum}

SELECT DISTINCT POST_CATEGORY
  FROM POST_INFO
 WHERE POST_USER_NUM = #{postUserNum}

	SELECT POST_NUM, POST_CATEGORY, POST_TITLE, POST_DATE, POST_USER_NUM, POST_KEYWORDS
	  FROM POST_INFO
	 WHERE POST_USER_NUM = #{postUserNum}
  ORDER BY POST_DATE DESC limit 1;
